// コースの「旬度」を判定（0:行けない, 1:行ける, 2:ベストシーズン, 3:特別なハイライト）
function getSeasonScore(course) {
  const m = getCurrentMonth();
  const months = parseSeasonMonths(course.season);
  if (!months.includes(m)) return 0; // 行けない

  const hl = COURSE_HIGHLIGHTS[course.name];
  if (hl && hl[m]) return 3; // 特別なハイライト

  // ベストシーズン判定（season文字列の中央月付近）
  if (months.length > 0 && months.length < 12) {
    const midIdx = Math.floor(months.length / 2);
    const bestRange = months.slice(Math.max(0, midIdx - 1), midIdx + 2);
    if (bestRange.includes(m)) return 2;
  }

  return 1; // 行ける
}

// コースのハイライトテキストを取得
function getHighlightText(course) {
  const m = getCurrentMonth();
  const hl = COURSE_HIGHLIGHTS[course.name];
  if (hl && hl[m]) return hl[m];
  return null;
}

